create table CCOMPONENT 
(
 IDCOMPONENT INTEGER not null,
 NMCOMPONENT VARCHAR2(64) not null,
 constraint PK_CCOMPONENT primary key (IDCOMPONENT)
);

create table PCONFIGPARAM 
(
 IDPARAM INTEGER not null,
 IDCOMPONENT INTEGER not null,
 NMPARAM VARCHAR2(64) not null,
 DSCPARAM VARCHAR2(255),
 TYPARAM SMALLINT not null,
 PRMSOURCE SMALLINT not null,
 ISHIDDEN SMALLINT default 0 not null,
 constraint PK_PCONFIGPARAM primary key (IDPARAM),
 constraint FK_COMPONENTPARAM_IDCOMPONENT foreign key (IDCOMPONENT)
 references CCOMPONENT (IDCOMPONENT)
);

create table DCONFIGSETTING
(
 IDCONFIGSETTING INTEGER not null,
 IDPARAM INTEGER not null,
 DTVALIDFROM DATE not null,
 DTVALIDTO DATE,
 LOGMODIFIEDBY VARCHAR2(8),
 DTCREATED DATE not null,
 constraint PK_DCONFIGSETTING primary key (IDCONFIGSETTING),
 constraint FK_PARAMSETTING_IDCONFIGPARAM foreign key (IDPARAM)
 references PCONFIGPARAM (IDPARAM)
);

create table DSETTINGVALUE 
(
 IDVALUE INTEGER not null,
 IDCONFIGSETTING INTEGER not null,
 TXTVALUE CLOB,
 DTCREATED DATE,
 constraint PK_DSETTINGVALUE primary key (IDVALUE),
 constraint FK_SETTINGVALUE_IDSETTING foreign key (IDCONFIGSETTING)
 references DCONFIGSETTING (IDCONFIGSETTING)
);

